data.examples#Examples:
function log(id: number) {
console.log(id);
}

const debounced = debounce({ delay: 1000 }, log);

debounced(1); // ignored
debounced(2); // ignored
debounced(3); // ignored

setTimeout(function () {
debounced(4); // 4
debounced(5); // ignored
}, 1000);

const debounced = debounce({ delay: 1000, immediate: true }, log);

debounced(1); // 1
debounced(2); // ignored
debounced(3); // ignored

setTimeout(function () {
debounced(4); // 4
debounced(5); // ignored
}, 1000);

